import { useState } from "react";
import "./App.css";

function App() {
  let [people, setPeople] = useState([
    { name: "max", points: 0 },
    { name: "erin", points: 3 },
  ]);

  let list = [];

  for (let i = 0; i < people.length; i++) {
    list.push(
      <div>
        <button
          onClick={() => {
            people[0][1] = people[0][1] + 1;
            setPeople(people);

            console.log(list);
          }}
        >
          +1
        </button>

        <p>{people[i][0]}</p>
        <p>{people[i][1]}</p>
      </div>
    );
  }

  return <div> </div>;
}

export default App;
